 * Internal constants used by the Amber KSP processor.
 */
internal object GenerationConstants {
    /** Standard header placed at the top of generated files. */
    const val GENERATED_HEADER: String = "// Generated by Amber KSP processor. Do not edit."

    /** File-level suppress annotation. */
    const val SUPPRESS_ANNOTATION: String =
        """@file:Suppress("ktlint", "RedundantNullableReturnType", "UNUSED_PARAMETER", "LocalVariableName")"""

    /** Indentation used in generated code. */
    const val INDENT: String = "    "
}
